import { useState, useEffect } from "react";
import "./DeviceManager.css";

const DeviceManager = () => {
  const [devices, setDevices] = useState([]);
  const [editing, setEditing] = useState(false);
  const [newName, setNewName] = useState("");
  const [userInput, setUserInput] = useState("");
  const [device, setDevice] = useState(null);

  useEffect(() => {
    fetch("/devices")
      .then((res) => {
        if (!res.ok) {
          throw new Error(`HTTP error ${res.status}`);
        }
        return res.json();
      })
      .then((data) => {
        const defaultDevices = data.filter((device) =>
          device.name.startsWith("default")
        );
        // Add numbers to device names
        defaultDevices.forEach((device, index) => {
          device.name = `default_${index + 1}`;
        });
        setDevices(defaultDevices);
      })
      .catch((err) => console.error("Error fetching devices:", err));
  }, []);

  const handleEdit = (device) => {
    setEditing(true);
    setDevice(device);
    setNewName(device.name);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  
    fetch(`/devices/max/${newName}_${userInput}`)
      .then((res) => {
        if (!res.ok) {
          throw new Error(`HTTP error ${res.status}`);
        }
        return res.json();
      })
      .then((maxDevice) => {
        const maxNumber = maxDevice
          ? parseInt(maxDevice.name.split("_")[2])
          : 0;
        const nextName = `${newName}_${userInput}_${maxNumber + 1}`;
  
        fetch(`/devices/${device._id}`, {
          method: "PATCH",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ name: nextName }),
        })
          .then((res) => {
            if (!res.ok) {
              throw new Error(`HTTP error ${res.status}`);
            }
            return res.json();
          })
          .then((updatedDevice) => {
            setEditing(false);
            setDevice(updatedDevice);
            setDevices(
              devices.map((dev) =>
                dev._id === updatedDevice._id ? updatedDevice : dev
              )
            );
          })
          .catch((err) => console.error("Error updating device:", err));
      })
      .catch((err) => console.error("Error fetching max device:", err));
  };

  return (
    <div>
      {devices.map((device) => (
        <div key={device._id} className="unRegDev">
          <h3>{device.name}</h3>
          <h4>{device._id}</h4>
          <button onClick={() => handleEdit(device)}>Edit</button>
        </div>
      ))}

      {editing && (
        <form className="DevForm" onSubmit={handleSubmit}>
          <h3>New Name:</h3>
          <select value={newName} onChange={(e) => setNewName(e.target.value)}>
            <option value="CAR">CAR</option>
            <option value="OFFICE">OFFICE</option>
            <option value="FACTORY">FACTORY</option>
          </select>
          <input
            type="text"
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
            placeholder="Enter a name"
          />
          <button type="submit">Submit</button>
        </form>
      )}
    </div>
  );
};

export { DeviceManager };
