import React from "react";
import "./Modal.css";

function Modal({ device, boards, states, onClose }) {
  const checkState = (sensor) => {
    const state = states.find(
      (state) =>
        state.device_name.lowerCase === device.Name.lowerCase() &&
        state.sensor_name.lowerCase === sensor.name.lowerCase()
    );
    if (state) {
      switch (state.condition) {
        case "<":
          return sensor.value < state.parameter ? state.name : "";
        case ">":
          return sensor.value > state.parameter ? state.name : "";
        // Add other conditions as needed
        default:
          return "";
      }
    } else {
      return "";
    }
  };

  // Get the most recent board data for this device
  const deviceBoards = boards.filter((board) => board.device_id === device._id);
  deviceBoards.sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp));
  const recentBoard = deviceBoards[0];

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close-button" onClick={onClose}>
          &times;
        </span>
        <h2>{device.Name}</h2>
        {recentBoard &&
          recentBoard.sensors.map((sensor) => (
            <div key={sensor.name}>
              {sensor.name}: {sensor.value} | State: {checkState(sensor)}
            </div>
          ))}
      </div>
    </div>
  );
}

export default Modal;
