import React, { useState, useEffect } from "react";
import "./States.css";

function States() {
  const [states, setStates] = useState([]);
  const [devices, setDevices] = useState([]);
  const [sensors, setSensors] = useState([]);
  const [selectedDevice, setSelectedDevice] = useState("");
  const [newState, setNewState] = useState({
    name: "",
    device_name: "",
    condition: "",
    sensor_name: "",
    parameter: "",
  });

  useEffect(() => {
    fetch("/states")
      .then((res) => {
        if (!res.ok) {
          throw new Error(`HTTP error ${res.status}`);
        }
        return res.json();
      })
      .then((data) => setStates(data))
      .catch((err) => console.error("Error fetching states:", err));

    fetch("/devices")
      .then((res) => {
        if (!res.ok) {
          throw new Error(`HTTP error ${res.status}`);
        }
        return res.json();
      })
      .then((data) => setDevices(data))
      .catch((err) => console.error("Error fetching devices:", err));
  }, []);

  const handleInputChange = (e) => {
    setNewState({ ...newState, [e.target.name]: e.target.value });

    if (e.target.name === "device_name") {
      setSelectedDevice(e.target.value);
      fetch(`/boards/${e.target.value}`)
        .then((res) => {
          if (!res.ok) {
            throw new Error(`HTTP error ${res.status}`);
          }
          return res.json();
        })
        .then((data) => {
          const latestData = data.sort(
            (a, b) => new Date(b.timestamp) - new Date(a.timestamp)
          );
          setSensors(latestData[0]?.sensors.map((sensor) => sensor.name) || []);
        })
        .catch((err) => console.error("Error fetching boards:", err));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch("/states", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newState),
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error(`HTTP error ${res.status}`);
        }
        return res.json();
      })
      .then((data) => {
        setStates([...states, data]);
        setNewState({
          name: "",
          device_name: "",
          condition: "",
          sensor_name: "",
          parameter: "",
        });
      })
      .catch((err) => console.error("Error submitting new state:", err));
  };

  return (
    <div className="states-page">
      <h1>States</h1>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Device</th>
            <th>Sensor</th>
            <th>Condition</th>
            <th>Parameter</th>
          </tr>
        </thead>
        <tbody>
          {states.map((state) => (
            <tr key={state._id}>
              <td>{state.name}</td>
              <td>{state.device_name}</td>
              <td>{state.sensor_name}</td>
              <td>{state.condition}</td>
              <td>{state.parameter}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <form onSubmit={handleSubmit}>
        <h2>Add State</h2>
        <div>
          <label htmlFor="state-name">Name:</label>
          <input
            type="text"
            id="state-name"
            name="name"
            value={newState.name}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label htmlFor="device-name">Device:</label>
          <select
            id="device-name"
            name="device_name"
            value={newState.device_name}
            onChange={handleInputChange}
            required
          >
            <option value="">Select a device</option>
            {devices.map((device) => (
              <option key={device._id} value={device.Name}>
                {device.Name}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label htmlFor="sensor-name">Sensor:</label>
          <select
            id="sensor-name"
            name="sensor_name"
            value={newState.sensor_name}
            onChange={handleInputChange}
            required
          >
            <option value="">Select a sensor</option>
            {selectedDevice &&
              sensors.map((sensor) => (
                <option key={sensor} value={sensor}>
                  {sensor}
                </option>
              ))}
          </select>
        </div>
        <div>
          <label htmlFor="condition">Condition:</label>
          <select
            id="condition"
            name="condition"
            value={newState.condition}
            onChange={handleInputChange}
            required
          >
            <option value="<">{'<'}</option>
            <option value=">">{'>'}</option>
            <option value="<=">{'<='}</option>
            <option value=">=">{'>='}</option>
            <option value="=">{'='}</option>
          </select>
        </div>
        <div>
          <label htmlFor="parameter">Parameter:</label>
          <input
            type="text"
            id="parameter"
            name="parameter"
            value={newState.parameter}
            onChange={handleInputChange}
            required
          />
        </div>
        <button type="submit">Add State</button>
      </form>
    </div>
  );
  
}

export { States };
