import React, { useState, useEffect } from 'react';

const DeviceManager = () => {
  const [devices, setDevices] = useState([]);
  const [editedNames, setEditedNames] = useState({});
  const [editing, setEditing] = useState({});

  useEffect(() => {
    fetch("/devices")
      .then((res) => {
        if (!res.ok) {
          throw new Error(`HTTP error ${res.status}`);
        }
        return res.json();
      })
      .then((data) => {
        setDevices(data.filter(device => device.name === "default"));
        const newEditedNames = data.reduce((acc, device) => ({...acc, [device._id]: device.name}), {});
        setEditedNames(newEditedNames);
      })
      .catch((err) => console.error("Error fetching devices:", err));
  }, []);

  const handleEditClick = (id) => {
    setEditing({...editing, [id]: true});
  };

  const handleNameChange = (id, newName) => {
    setEditedNames({...editedNames, [id]: newName});
  };

  const handleNameSubmit = (id) => {
    fetch(`/devices/${id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({name: editedNames[id]}),
    })
    .then(response => response.json())
    .then(data => {
      setDevices(devices.map(device => device._id === id ? data : device));
      setEditing({...editing, [id]: false});
    })
    .catch((error) => {
      console.error('Error:', error);
    });
  };

  return (
    <div>
      <h1>Device Manager</h1>
      {devices.map(device => (
        <div key={device._id}>
          <h2>{device.name}</h2>
          <p>{device._id}</p>
          {editing[device._id] ? (
            <>
              <input type="text" value={editedNames[device._id] || ""} onChange={(e) => handleNameChange(device._id, e.target.value)} />
              <button onClick={() => handleNameSubmit(device._id)}>Submit</button>
            </>
          ) : (
            <button onClick={() => handleEditClick(device._id)}>Edit</button>
          )}
        </div>
      ))}
    </div>
  );
};

export {DeviceManager};
