import React, { useEffect, useState } from "react";
import "./Modal.css";

function Modal({ device, states, onClose }) {
  const [boards, setBoards] = useState([]);

  useEffect(() => {
    fetch("/boards")
      .then((response) => response.json())
      .then((data) => setBoards(data));
  }, []);

  

  // Get the most recent board data for this device
  const deviceBoards = boards.filter((board) => board.device_id === device._id);
  deviceBoards.sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp));
  const recentBoard = deviceBoards[0];

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close-button" onClick={onClose}>
          &times;
        </span>
        <h2>{device.Name}</h2>
        {recentBoard && (
          <table>
            <thead>
              <tr>
                <th>Sensor Name</th>
                <th>Value</th>
                <th>State</th>
              </tr>
            </thead>
            <tbody>
              {recentBoard.sensors.map((sensor) => {
                const stateCheck = checkState(sensor);
                return (
                  <React.Fragment key={sensor.name}>
                    <tr>
                      <td>{sensor.name}</td>
                      <td>
                        {Array.isArray(sensor.value)
                          ? sensor.value.join(", ")
                          : sensor.value}
                      </td>
                      <td>
                        {typeof stateCheck === "object"
                          ? stateCheck.state
                          : stateCheck}
                      </td>
                    </tr>
                    {typeof stateCheck === "object" &&
                      stateCheck.values.map((val, index) => (
                        <tr key={val}>
                          <td></td>
                          <td>
                            {val}: {stateCheck.data[index]}
                          </td>
                          <td></td>
                        </tr>
                      ))}
                  </React.Fragment>
                );
              })}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default Modal;
