import React, { useEffect, useState } from "react";
import "./Query.css";

function AverageComponent({ device_id, sensor_name }) {
  const [timePeriod, setTimePeriod] = useState("");
  const [average, setAverage] = useState("");

  const handleInputChange = (event) => {
    setTimePeriod(event.target.value);
  };

  const handleAverageCalculation = () => {
    fetch(
      `/boards/device/${device_id}/sensor/${sensor_name}?timePeriod=${timePeriod}`
    )
      .then((response) => {
        console.log(response);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        setAverage(data.average);
      })
      .catch((error) => {
        console.log("There was an error!", error);
      });
  };

  return (
    <div>
      <input
        type="number"
        placeholder="Enter time in seconds"
        value={timePeriod}
        onChange={handleInputChange}
      />
      <button onClick={handleAverageCalculation}>Calculate Average</button>
      {average && <p>Average: {average}</p>}
    </div>
  );
}

function Query() {
  const [devices, setDevices] = useState([]);

  useEffect(() => {
    fetch("/devices")
      .then((response) => response.json())
      .then((data) => {
        if (data) {
          setDevices(data);
        }
      });
  }, []);

  useEffect(() => {
    fetchSensorsForDevices();
  }, [devices]);

  const fetchSensorsForDevices = () => {
    devices.forEach((device) => {
      fetch(`/boards/${device._id}`)
        .then((response) => response.json())
        .then((sensors) => {
          const updatedDevices = devices.map((d) => {
            if (d._id === device._id) {
              return {
                ...d,
                sensors: sensors.map((sensor) => sensor.name),
              };
            }
            return d;
          });
          setDevices(updatedDevices);
        })
        .catch((error) => {
          console.error(`Error fetching sensors for device ${device._id}:`, error);
        });
    });
  };

  return (
    <div className="Query">
      <h2>All Devices:</h2>
      {devices.map((device) => (
        <div key={device._id}>
          <p>Device Name: {device.name}</p>
          <p>Sensors: {device.sensors ? device.sensors.join(", ") : "Loading..."}</p>
        </div>
      ))}
    </div>
  );
}


export { Query };
