import React, { useState, useEffect } from "react";
import "./States.css";

function States() {
  const [states, setStates] = useState([]);
  const [devices, setDevices] = useState([]);
  const [sensors, setSensors] = useState([]);
  const [newState, setNewState] = useState({
    name: "",
    device_name: "",
    condition: "",
    sensor_name: "",
    parameter: "",
  });

  const [sensorAttributes, setSensorAttributes] = useState([]);

  useEffect(() => {
    fetch("/states")
      .then((res) => {
        if (!res.ok) {
          throw new Error(`HTTP error ${res.status}`);
        }
        return res.json();
      })
      .then((data) => setStates(data))
      .catch((err) => console.error("Error fetching states:", err));

    fetch("/devices")
      .then((res) => {
        if (!res.ok) {
          throw new Error(`HTTP error ${res.status}`);
        }
        return res.json();
      })
      .then((data) => setDevices(data))
      .catch((err) => console.error("Error fetching devices:", err));
  }, []);

  const handleDeviceChange = (e) => {
    const selectedDeviceId = e.target.value;
    setNewState({ ...newState, device_name: selectedDeviceId });

    fetch(`/boards/${selectedDeviceId}`)
      .then((res) => {
        if (!res.ok) {
          throw new Error(`HTTP error ${res.status}`);
        }
        return res.json();
      })
      .then((sensorNames) => setSensors(sensorNames))
      .catch((err) => console.error("Error fetching device sensors:", err));
  };

  const handleInputChange = (e) => {
    setNewState({ ...newState, [e.target.name]: e.target.value });
  };

  const handleSensorChange = (e) => {
    const selectedSensor = e.target.value;
    setNewState({ ...newState, sensor_name: selectedSensor });

    if (selectedSensor === "BME") {
      setSensorAttributes(["temperature", "humidity", "pressure", "gas"]);
    } else if (selectedSensor === "NPU") {
      setSensorAttributes([
        "Acc-X",
        "Acc-Y",
        "Acc-Z",
        "Gyro-X",
        "Gyro-Y",
        "Gyro-Z",
      ]);
    } else {
      setSensorAttributes([]);
    }
  };

  const handleAttributeChange = (e) => {
    const selectedAttribute = e.target.value;
    setNewState((prevState) => ({
      ...prevState,
      sensor_name: prevState.sensor_name + "-" + selectedAttribute,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Find the device name using the device id
    const device = devices.find(
      (device) => device._id === newState.device_name
    );
    const deviceName = device ? device.name : "";

    // Create a new object with the device name instead of id
    const stateToSubmit = {
      ...newState,
      device_name: deviceName,
    };

    fetch("/states", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(stateToSubmit),
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error(`HTTP error ${res.status}`);
        }
        return res.json();
      })
      .then((data) => {
        setStates([...states, data]);
        setNewState({
          name: "",
          device_name: "",
          condition: "",
          sensor_name: "",
          parameter: "",
        });
      })
      .catch((err) => console.error("Error submitting new state:", err));
  };

  return (
    <div className="states-page">
      <h1>States</h1>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Device</th>
            <th>Sensor</th>
            <th>Condition</th>
            <th>Parameter</th>
          </tr>
        </thead>
        <tbody>
          {states.map((state) => (
            <tr key={state._id}>
              <td>{state.name}</td>
              <td>{state.device_name}</td>
              <td>{state.sensor_name}</td>
              <td>{state.condition}</td>
              <td>{state.parameter}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <form onSubmit={handleSubmit}>
        <h2>Add State</h2>
        <div>
          <label htmlFor="state-name">Name:</label>
          <input
            type="text"
            id="state-name"
            name="name"
            value={newState.name}
            onChange={handleInputChange}
            required
          />
        </div>
        {newState.name && (
          <div>
            <label htmlFor="device-name">Device:</label>
            <select
              id="device-name"
              name="device_name"
              value={newState.device_name}
              onChange={handleDeviceChange}
              required
            >
              <option value="">Select device</option>
              {devices.map((device) => (
                <option key={device._id} value={device._id}>
                  {device.name}
                </option>
              ))}
            </select>
          </div>
        )}
        {newState.device_name && (
          <div>
            <label htmlFor="sensor-name">Sensor:</label>
            <select
              id="sensor-name"
              name="sensor_name"
              value={newState.sensor_name}
              onChange={handleInputChange}
              required
            >
              <option value="">Select sensor</option>
              {sensors.map((sensor) => (
                <option key={sensor} value={sensor}>
                  {sensor}
                </option>
              ))}
            </select>
          </div>
        )}



        {newState.sensor_name && (
          <div>
            <label htmlFor="condition">Condition:</label>
            <select
              id="condition"
              name="condition"
              value={newState.condition}
              onChange={handleInputChange}
              required
            >
              <option value="">Select a condition</option>
              <option value="<">{"<"}</option>
              <option value=">">{">"}</option>
              <option value="<=">{"<="}</option>
              <option value=">=">{">="}</option>
              <option value="=">{"="}</option>
            </select>
          </div>
        )}
        {newState.condition && (
          <div>
            <label htmlFor="parameter">Parameter:</label>
            <input
              type="number"
              step="any"
              id="parameter"
              name="parameter"
              value={newState.parameter}
              onChange={handleInputChange}
              required
            />
          </div>
        )}
        {newState.parameter && (
          <div>
            <button type="submit">Add State</button>
          </div>
        )}
      </form>
    </div>
  );
}

export { States };
