import React, { useEffect, useState } from "react";
import "./Modal.css";

function Modal({ device, states, onClose }) {
  const [boards, setBoards] = useState([]);

  useEffect(() => {
    fetch("/boards")
      .then((response) => response.json())
      .then((data) => setBoards(data));
  }, []);

  const checkState = (sensor) => {
    let selectedSensor = sensor.name;
    if (Array.isArray(selectedSensor)) {
      selectedSensor = selectedSensor.join(", ");
    }
    let sensorValue = sensor.value;
    console.log(sensorValue); // Add this line
    if (Array.isArray(sensorValue)) {
      sensorValue = sensorValue.join(", ");
    }
  
    const matchingStates = states.filter(
      (state) =>
        state.device_name &&
        selectedSensor &&
        state.device_name.toLowerCase() === device.Name.toLowerCase() &&
        state.sensor_name.toLowerCase() === selectedSensor.toLowerCase()
    );
  
    for (let state of matchingStates) {
      switch (state.condition) {
        case "<":
          if (sensor.value < state.parameter) return state.name;
          break;
        case ">":
          if (sensor.value > state.parameter) return state.name;
          break;
        case "<=":
          if (sensor.value <= state.parameter) return state.name;
          break;
        case ">=":
          if (sensor.value >= state.parameter) return state.name;
          break;
        case "=":
          if (sensor.value === state.parameter) return state.name;
          break;
        default:
          break;
      }
    }
  
    if (sensor.name === "BME" && sensor.type === "i2c" && Array.isArray(sensor.value) && sensor.value.length === 4) {
      return {
        state: "BME",
        values: ["Temperature", "Humidity", "Pressure", "Gas"],
        data: sensor.value
      };
    } else if (sensor.name === "NPU" && sensor.type === "i2c" && Array.isArray(sensor.value) && sensor.value.length === 6) {
      return {
        state: "NPU",
        values: ["Acc-X", "Acc-Y", "Acc-Z", "Gyro-X", "Gyro-Y", "Gyro-Z"],
        data: sensor.value
      };
    }
  
    return "Default";
  };
  

  // Get the most recent board data for this device
  const deviceBoards = boards.filter((board) => board.device_id === device._id);
  deviceBoards.sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp));
  const recentBoard = deviceBoards[0];

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close-button" onClick={onClose}>
          &times;
        </span>
        <h2>{device.Name}</h2>
        {recentBoard && (
          <table>
            <thead>
              <tr>
                <th>Sensor Name</th>
                <th>Value</th>
                <th>State</th>
              </tr>
            </thead>
            <tbody>
              {recentBoard.sensors.map((sensor) => (
                <tr key={sensor.name}>
                  <td>{sensor.name}</td>
                  <td>
                    {Array.isArray(sensor.value)
                      ? sensor.value.join(", ")
                      : sensor.value}
                  </td>
                  <td>{checkState(sensor)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default Modal;
