import React, { useEffect, useState } from "react";
import "./Query.css";
import io from "socket.io-client";

function AverageComponent({ device_id, sensor_name }) {
  const [timePeriod, setTimePeriod] = useState("");
  const [average, setAverage] = useState("");

  const handleInputChange = (event) => {
    setTimePeriod(event.target.value);
  };

  const handleAverageCalculation = () => {
    fetch(
      `/boards/device/${device_id}/sensor/${sensor_name}?timePeriod=${timePeriod}`
    )
      .then((response) => {
        console.log(response);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        setAverage(data.average);
      })
      .catch((error) => {
        console.log("There was an error!", error);
      });
  };

  return (
    <div>
      <input
        type="number"
        placeholder="Enter time in seconds"
        value={timePeriod}
        onChange={handleInputChange}
      />
      <button onClick={handleAverageCalculation}>Calculate Average</button>
      {average && <p>Average: {average}</p>}
    </div>
  );
}

function Query() {
  const [boards, setBoards] = useState([]);
  const [devices, setDevices] = useState([]);
  const [states, setStates] = useState([]);
  const [selectedScenario, setSelectedScenario] = useState('ALL');
  const [selectedDevice, setSelectedDevice] = useState('ALL');
  const [selectedState, setSelectedState] = useState('ALL');
  const [uniqueStates, setUniqueStates] = useState([]);

  useEffect(() => {
    fetch("/boards")
      .then(response => response.json())
      .then(data => setBoards(data))
      .catch(error => console.error('Error:', error));
  }, []);

  useEffect(() => {
    fetch("/devices")
      .then(response => response.json())
      .then(data => setDevices(data))
      .catch(error => console.error('Error:', error));
  }, []);

  useEffect(() => {
    fetch("/states")
      .then(response => response.json())
      .then(data => {
        setStates(data);
        let uniqueNames = Array.from(new Set(data.map(s => s.name)));
        setUniqueStates(uniqueNames);
      })
      .catch(error => console.error('Error:', error));
  }, []);

  const checkStateForIndividualSensor = (deviceName, sensorName, sensorValue) => {
    const matchingStates = states.filter((state) => {
      return (
        state.device_name.toLowerCase() === deviceName.toLowerCase() &&
        state.sensor_name.toLowerCase() === sensorName.toLowerCase()
      );
    });

    let result = "Default";

    for (let state of matchingStates) {
      switch (state.condition) {
        case "<":
          if (sensorValue < state.parameter) result = state.name;
          break;
        case ">":
          if (sensorValue > state.parameter) result = state.name;
          break;
        case "<=":
          if (sensorValue <= state.parameter) result = state.name;
          break;
        case ">=":
          if (sensorValue >= state.parameter) result = state.name;
          break;
        case "=":
          if (sensorValue === state.parameter) result = state.name;
          break;
        case "idle":
          if (sensorValue === state.parameter) result = state.name;
          break;
        default:
          break;
      }
    }

    return result;
  };

  const displaySensors = (sensor, index, board) => {
    const deviceName = devices.find(device => device._id === board.device_id)?.name;

    if (sensor.name === "BME" || sensor.name === "MPU") {
      const sensorProperties =
        sensor.name === "BME"
          ? ["Temperature", "Humidity", "Pressure", "Gas"]
          : ["Acc-X", "Acc-Y", "Acc-Z", "Gyro-X", "Gyro-Y", "Gyro-Z"];
      return sensor.value.map((val, index) => {
        const sensorName = `${sensor.name}-${sensorProperties[index]}`;
        const state = checkStateForIndividualSensor(deviceName, sensorName, val);
        return (
          <div key={`${sensor.name}-${index}`}>
            <span>{sensorName}</span>
            <span>{val}</span>
            <span>{state}</span>
            <AverageComponent device_id={board.device_id} sensor_name={sensorName} />
          </div>
        );
      });
    } else {
      const state = checkStateForIndividualSensor(deviceName, sensor.name, sensor.value);
      return (
        <div key={`${sensor.name}-${index}`}>
          <span>{sensor.name}</span>
          <span>{sensor.value}</span>
          <span>{state}</span>
          <AverageComponent device_id={board.device_id} sensor_name={sensor.name} />
        </div>
      );
    }
  };

  const displayBoards = (board, index) => {
    if (selectedState !== 'ALL') {
      board.sensors = board.sensors.filter(sensor => {
        const deviceName = devices.find(device => device._id === board.device_id)?.name;
        const state = sensor.name === "BME" || sensor.name === "MPU" ? checkStateForIndividualSensor(deviceName, `${sensor.name}-Temperature`, sensor.value[0]) : checkStateForIndividualSensor(deviceName, sensor.name, sensor.value);
        return state === selectedState;
      });
      if (board.sensors.length === 0) {
        return null;
      }
    }

    return (
      <div key={index}>
        <h2>{devices.find(device => device._id === board.device_id)?.name}</h2>
        {board.sensors.map((sensor, index) => displaySensors(sensor, index, board))}
      </div>
    );
  };

  return (
    <div className="App">
      <div>
        <label>Filter by scenario: </label>
        <select onChange={(e) => setSelectedScenario(e.target.value)} value={selectedScenario}>
          <option value="ALL">ALL</option>
          <option value="SCENARIO1">SCENARIO1</option>
          <option value="SCENARIO2">SCENARIO2</option>
          <option value="SCENARIO3">SCENARIO3</option>
        </select>
      </div>

      <div>
        <label>Filter by device: </label>
        <select onChange={(e) => setSelectedDevice(e.target.value)} value={selectedDevice}>
          <option value="ALL">ALL</option>
          {devices.map((device, index) => (
            <option key={index} value={device._id}>
              {device.name}
            </option>
          ))}
        </select>
      </div>

      {
        selectedScenario !== 'ALL' && (
          <div>
            <label>Filter by state: </label>
            <select onChange={(e) => setSelectedState(e.target.value)} value={selectedState}>
              <option value="ALL">ALL</option>
              {uniqueStates.map((state, index) => (
                <option key={index} value={state}>
                  {state}
                </option>
              ))}
            </select>
          </div>
        )
      }

      {
        selectedState !== 'ALL' && (
          <div>
            <h2>Devices with a sensor in state "{selectedState}":</h2>
            <p>
              {
                boards.filter(board => 
                  board.sensors.some(sensor => {
                    const deviceName = devices.find(device => device._id === board.device_id)?.name;
                    const state = sensor.name === "BME" || sensor.name === "MPU" ? checkStateForIndividualSensor(deviceName, `${sensor.name}-Temperature`, sensor.value[0]) : checkStateForIndividualSensor(deviceName, sensor.name, sensor.value);
                    return state === selectedState;
                  })
                ).length
              }
            </p>
          </div>
        )
      }

      {
        selectedScenario === 'ALL' && selectedDevice === 'ALL'
          ? boards.map((board, index) => displayBoards(board, index))
          : boards
              .filter(board => selectedDevice === 'ALL' || board.device_id === selectedDevice)
              .filter(board => {
                const deviceName = devices.find(device => device._id === board.device_id)?.name;
                return selectedScenario === 'ALL' || states.some(state => state.scenario === selectedScenario && state.device_name === deviceName);
              })
              .map((board, index) => displayBoards(board, index))
      }
    </div>
  );
}

export default Query;

export { Query };
