import React, { useState, useEffect } from "react";
import "./Dashboard.css";

function Dashboard() {
  const [devices, setDevices] = useState([]);

  useEffect(() => {
    fetch("/devices")
      .then((res) => {
        if (!res.ok) {
          throw new Error(`HTTP error ${res.status}`);
        }
        return res.json();
      })
      .then((data) => setDevices(data))
      .catch((err) => console.error("Error fetching devices:", err));
  }, []);

  const handleClick = (deviceId) => {
    fetch(`/boards/${deviceId}`)
      .then((res) => {
        if (!res.ok) {
          throw new Error(`HTTP error ${res.status}`);
        }
        return res.json();
      })
      .then((data) => console.log(data))
      .catch((err) =>
        console.error(`Error fetching data for device ${deviceId}:`, err)
      );
  };

  return (
    <div className="dashboard-page">
      <h1>Dashboard</h1>
      <div className="devices">
        {devices.map((device) => (
          <div className="device" key={device._id}>
            <h2>{device.Name}</h2>
            <p>Sensors: {device.SensorList}</p>
            <button onClick={() => handleClick(device._id)}>View Data</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export { Dashboard };
