import React, { useEffect, useState } from "react";
import "./Modal.css";

function Modal({ device, states, onClose }) {
  const [boards, setBoards] = useState([]);

  useEffect(() => {
    fetch("/boards")
      .then((response) => response.json())
      .then((data) => setBoards(data));
  }, []);

  const checkState = (sensor) => {
    const matchingStates = states.filter(
      (state) =>
        state.device_name.toLowerCase() === device.Name.toLowerCase() &&
        state.sensor_name.toLowerCase() === sensor.name.toLowerCase()
    );

    for (let state of matchingStates) {
      for (let value of sensor.values) {
        switch (state.condition) {
          case "<":
            if (value < state.parameter) return state.name;
            break;
          case ">":
            if (value > state.parameter) return state.name;
            break;
          case "<=":
            if (value <= state.parameter) return state.name;
            break;
          case ">=":
            if (value >= state.parameter) return state.name;
            break;
          case "=":
            if (value === state.parameter) return state.name;
            break;
          default:
            break;
        }
      }
    }
    return "Default";
  };

  // Get the most recent board data for this device
  const deviceBoards = boards.filter((board) => board.device_id === device._id);
  deviceBoards.sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp));
  const recentBoard = deviceBoards[0];

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close-button" onClick={onClose}>
          &times;
        </span>
        <h2>{device.Name}</h2>
        {recentBoard && (
          <table>
            <thead>
              <tr>
                <th>Sensor Name</th>
                <th>Value</th>
                <th>State</th>
              </tr>
            </thead>
            <tbody>
              {recentBoard.sensors.map((sensor) => (
                <tr key={sensor.name}>
                  <td>{sensor.name}</td>
                  <td>
                    {Array.isArray(sensor.value)
                      ? sensor.value.join(", ")
                      : sensor.value}
                  </td>
                  <td>{checkState(sensor)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default Modal;
