import React, { useEffect, useState } from "react";
import "./Query.css";

function Query() {
  const [devices, setDevices] = useState([]);
  const [states, setStates] = useState([]);
  const [boards, setBoards] = useState([]);
  const [selectedDevice, setSelectedDevice] = useState("");
  const [selectedState, setSelectedState] = useState({});

  useEffect(() => {
    fetch("/devices")
      .then((response) => response.json())
      .then((data) => {
        setDevices(data);
        setSelectedDevice(data[0]?._id);
      });

    fetch("/states")
      .then((response) => response.json())
      .then((data) => setStates(data));

    fetch("/boards")
      .then((response) => response.json())
      .then((data) => setBoards(data));
  }, []);

  const handleDeviceSelect = (event) => {
    setSelectedDevice(event.target.value);
  };

  const handleStateSelect = (device, event) => {
    setSelectedState({
      ...selectedState,
      [device]: event.target.value,
    });
  };

  const checkStateForIndividualSensor = (
    sensorName,
    sensorValue,
    deviceName
  ) => {
    const matchingStates = states.filter((state) => {
      return (
        state.device_name.toLowerCase() === deviceName.toLowerCase() &&
        state.sensor_name.toLowerCase() === sensorName.toLowerCase()
      );
    });

    let result = "Default";

    for (let state of matchingStates) {
      switch (state.condition) {
        case "<":
          if (sensorValue < state.parameter) result = state.name;
          break;
        case ">":
          if (sensorValue > state.parameter) result = state.name;
          break;
        case "<=":
          if (sensorValue <= state.parameter) result = state.name;
          break;
        case ">=":
          if (sensorValue >= state.parameter) result = state.name;
          break;
        case "=":
          if (sensorValue === state.parameter) result = state.name;
          break;
        default:
          break;
      }
    }

    return result;
  };

  const deviceBoards = boards.filter(
    (board) => board.device_id === selectedDevice
  );
  deviceBoards.sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp));
  const recentBoard = deviceBoards[0];
  const deviceName = devices.find(
    (device) => device._id === selectedDevice
  )?.name;

  const displaySensors = (sensor, index) => {
    // If a state is selected, only display the sensor for that state
    console.log(
      sensor.name.toLowerCase().includes(selectedState[selectedDevice])
    );
    if (
      selectedState[selectedDevice] &&
      !sensor.name
        .toLowerCase()
        .includes(selectedState[selectedDevice].toLowerCase())
    )
      return null;

    if (
      (sensor.name === "BME" || sensor.name === "MPU") &&
      sensor.type === "i2c" &&
      Array.isArray(sensor.value)
    ) {
      const sensorProperties =
        sensor.name === "BME"
          ? ["Temperature", "Humidity", "Pressure", "Gas"]
          : ["Acc-X", "Acc-Y", "Acc-Z", "Gyro-X", "Gyro-Y", "Gyro-Z"];
      return sensor.value.map((val, index) => {
        const sensorName =
          `${sensor.name}-${sensorProperties[index]}`.toLowerCase();
        console.log(sensor)
        const state = checkStateForIndividualSensor(
          sensorName,
          val,
          deviceName
        );
        return (
          <div key={`${sensorName}-${index}`}>
            <p>Sensor Name: {sensorName}</p>
            <p>Sensor Value: {val}</p>
            <p>Sensor State: {state}</p>
          </div>
        );
      });
    } else {
      const sensorName = sensor.name.toLowerCase();
      const state = checkStateForIndividualSensor(
        sensorName,
        sensor.value,
        deviceName
      );
      return (
        <div key={`${sensor.name}-${index}`}>
          <p>Sensor Name: {sensorName}</p>
          <p>Sensor Value: {sensor.value}</p>
          <p>Sensor State: {state}</p>
        </div>
      );
    }
  };

  const deviceStates = states.filter(
    (state) => state.device_name.toLowerCase() === deviceName?.toLowerCase()
  );

  return (
    <div className="query">
      <div className="device-select">
        <label htmlFor="device-select">Select a device:</label>
        <select
          id="device-select"
          value={selectedDevice}
          onChange={handleDeviceSelect}
        >
          {devices.map((device) => (
            <option key={device._id} value={device._id}>
              {device.name}
            </option>
          ))}
        </select>
      </div>
      <div className="state-select">
        <label htmlFor="state-select">Select a state:</label>
        <select
          id="state-select"
          value={selectedState[selectedDevice] || ""}
          onChange={(e) => handleStateSelect(selectedDevice, e)}
        >
          <option value="">-- Select --</option>
          {deviceStates.map((state) => (
            <option key={state._id} value={state.name}>
              {state.name}
            </option>
          ))}
        </select>
      </div>
      <div className="board-data">
        <h2>Device: {deviceName}</h2>
        <div className="sensorList">
          {recentBoard?.sensors.map(displaySensors)}
        </div>
      </div>
    </div>
  );
}

export { Query };
