import React, { useState, useEffect } from "react";

function Query() {
  const [devices, setDevices] = useState([]);
  const [states, setStates] = useState([]);
  const [selectedDevice, setSelectedDevice] = useState("");
  const [selectedState, setSelectedState] = useState("");
  const [deviceData, setDeviceData] = useState([]);

  const sensorProperties = {
    BME: ["Temperature", "Humidity", "Pressure", "Gas"],
    NPU: ["Acc-X", "Acc-Y", "Acc-Z", "Gyro-X", "Gyro-Y", "Gyro-Z"],
  };

  useEffect(() => {
    fetch("/devices")
      .then((response) => response.json())
      .then((data) => setDevices(data));
    fetch("/states")
      .then((response) => response.json())
      .then((data) => {
        console.log('States:', data);
        setStates(data);
      });
  }, []);

  const handleDeviceChange = (event) => {
    setSelectedDevice(event.target.value);
    updateData();
  };

  const handleStateChange = (event) => {
    setSelectedState(event.target.value);
    updateData();
  };

  const updateData = () => {
    if (selectedDevice) {
      fetch(`/boards/${selectedDevice}`)
        .then((response) => response.json())
        .then((data) => {
          const filteredData = data.filter((board) =>
            checkStateForBoard(board)
          );
          setDeviceData(filteredData);
        });
    }
  };

  const checkStateForBoard = (board) => {
    for (let sensor of board.sensors) {
      if (sensor.type === "i2c" && Array.isArray(sensor.value)) {
        const sensorProps = sensorProperties[sensor.name];
        for (let i = 0; i < sensor.value.length; i++) {
          const sensorName = `${sensor.name}-${sensorProps[i]}`.toLowerCase();
          const sensorValue = sensor.value[i];
          if (checkStateForSensor(sensorName, sensorValue)) {
            return true;
          }
        }
      } else {
        if (checkStateForSensor(sensor.name, sensor.value)) {
          return true;
        }
      }
    }
    return false;
  };

  const checkStateForSensor = (sensorName, sensorValue) => {
    const matchingStates = states.filter(
      (state) =>
        state.device_name.toLowerCase() === selectedDevice.toLowerCase() &&
        state.sensor_name.toLowerCase() === sensorName.toLowerCase()
    );

    for (let state of matchingStates) {
      if (state.name === selectedState) {
        switch (state.condition) {
          case "<":
            return sensorValue < state.parameter;
          case ">":
            return sensorValue > state.parameter;
          case "<=":
            return sensorValue <= state.parameter;
          case ">=":
            return sensorValue >= state.parameter;
          case "=":
            return sensorValue === state.parameter;
          default:
            return false;
        }
      }
    }
    return false;
  };

  useEffect(updateData, [selectedDevice, selectedState]);

  return (
    <div>
      <h1>Query Page</h1>

      <select onChange={handleDeviceChange}>
        <option>--Select a device--</option>
        {devices.map((device) => (
          <option key={device._id} value={device._id}>
            {device.name}
          </option>
        ))}
      </select>

      <select onChange={handleStateChange}>
        <option>--Select a state--</option>
        {states
          .filter((state) => state.device_name === selectedDevice)
          .map((state) => (
            <option key={state._id} value={state.name}>
              {state.name}
            </option>
          ))}
      </select>

      {deviceData.map((board) => (
        <div key={board._id}>
          <h2>{new Date(board.timestamp).toLocaleString()}</h2>
          {board.sensors.map((sensor, index) => (
            <p key={index}>
              {sensor.name}: {sensor.value}
            </p>
          ))}
        </div>
      ))}
    </div>
  );
}

export default Query;
