import React, { useState, useEffect } from "react";
import "./States.css";

function States() {
  const [states, setStates] = useState([]);
  const [devices, setDevices] = useState([]);
  const [sensors, setSensors] = useState([]);
  const [newState, setNewState] = useState({
    name: "",
    device_name: "",
    condition: "",
    sensor_name: "",
    parameter: "",
  });

  useEffect(() => {
    fetch("/states")
      .then((res) => {
        if (!res.ok) {
          throw new Error(`HTTP error ${res.status}`);
        }
        return res.json();
      })
      .then((data) => setStates(data))
      .catch((err) => console.error("Error fetching states:", err));

    fetch("/devices")
      .then((res) => {
        if (!res.ok) {
          throw new Error(`HTTP error ${res.status}`);
        }
        return res.json();
      })
      .then((data) => setDevices(data))
      .catch((err) => console.error("Error fetching devices:", err));

  }, []);

  const handleDeviceChange = (e) => {
    const selectedDevice = e.target.value;
    setNewState({ ...newState, device_name: selectedDevice });

    fetch(`/boards/${selectedDevice}`)
    .then((res) => {
      if (!res.ok) {
        throw new Error(`HTTP error ${res.status}`);
      }
      return res.json();
    })
    .then((sensorNames) => setSensors(sensorNames))
    .catch((err) => console.error("Error fetching device sensors:", err));
  };

  const handleInputChange = (e) => {
    setNewState({ ...newState, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch("/states", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newState),
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error(`HTTP error ${res.status}`);
        }
        return res.json();
      })
      .then((data) => {
        setStates([...states, data]);
        setNewState({
          name: "",
          device_name: "",
          condition: "",
          sensor_name: "",
          parameter: "",
        });
      })
      .catch((err) => console.error("Error submitting new state:", err));
  };

  return (
    <div className="states-page">
      <h1>States</h1>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Device</th>
            <th>Sensor</th>
            <th>Condition</th>
            <th>Parameter</th>
          </tr>
        </thead>
        <tbody>
          {states.map((state) => (
            <tr key={state._id}>
              <td>{state.name}</td>
              <td>{state.device_name}</td>
              <td>{state.sensor_name}</td>
              <td>{state.condition}</td>
              <td>{state.parameter}</td>
            </tr>
          ))}
        </tbody>
      </table>
      
    </div>
  );
  
}

export { States };
