import React, { useEffect, useState } from "react";
import "./Query.css";

function Query() {
  const [devices, setDevices] = useState([]);
  const [states, setStates] = useState([]);
  const [boards, setBoards] = useState([]);
  const [selectedDevice, setSelectedDevice] = useState("");

  useEffect(() => {
    fetch("/devices")
      .then((response) => response.json())
      .then((data) => {
        setDevices(data);
        setSelectedDevice(data[0]?._id); // set the selected device to the first device by default
      });

    fetch("/states")
      .then((response) => response.json())
      .then((data) => setStates(data));

    fetch("/boards")
      .then((response) => response.json())
      .then((data) => setBoards(data));
  }, []);

  const handleDeviceSelect = (event) => {
    setSelectedDevice(event.target.value);
  };

  const checkStateForIndividualSensor = (
    sensorName,
    sensorValue,
    deviceName
  ) => {
    const matchingStates = states.filter((state) => {
      return (
        state.device_name.toLowerCase() === deviceName.toLowerCase() &&
        state.sensor_name.toLowerCase() === sensorName.toLowerCase()
      );
    });

    let result = "Default";

    for (let state of matchingStates) {
      switch (state.condition) {
        case "<":
          if (sensorValue < state.parameter) result = state.name;
          break;
        case ">":
          if (sensorValue > state.parameter) result = state.name;
          break;
        case "<=":
          if (sensorValue <= state.parameter) result = state.name;
          break;
        case ">=":
          if (sensorValue >= state.parameter) result = state.name;
          break;
        case "=":
          if (sensorValue === state.parameter) result = state.name;
          break;
        default:
          break;
      }
    }

    return result;
  };

  const deviceBoards = boards.filter(
    (board) => board.device_id === selectedDevice
  );
  deviceBoards.sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp));
  const recentBoard = deviceBoards[0];
  const deviceName = devices.find(
    (device) => device._id === selectedDevice
  )?.name;

  return (
    <div className="query">
      <div className="device-select">
        <label htmlFor="device-select">Select a device:</label>
        <select
          id="device-select"
          value={selectedDevice}
          onChange={handleDeviceSelect}
        >
          {devices.map((device) => (
            <option key={device._id} value={device._id}>
              {device.name}
            </option>
          ))}
        </select>
      </div>
      <div className="board-data">
        <h2>Latest Board Data</h2>
        {recentBoard ? (
          <div className="dev">
            <p>Timestamp: {new Date(recentBoard.timestamp).toLocaleString()}</p>
            <p>Device ID: {recentBoard.device_id}</p>
            {recentBoard.sensors.map((sensor, index) => (
              <div key={index}>
                <p>Sensor Name: {sensor.name}</p>
                <p>Sensor Value: {sensor.value}</p>
                <p>
                  Sensor State:{" "}
                  {checkStateForIndividualSensor(
                    sensor.name,
                    sensor.value,
                    deviceName
                  )}
                </p>
              </div>
            ))}
          </div>
        ) : (
          <p>No board data available for this device.</p>
        )}
      </div>
    </div>
  );
}

export { Query };
