function Dashboard() {
  const [devices, setDevices] = useState([]);
  const [states, setStates] = useState([]);

  useEffect(() => {
    fetch("/devices")
      .then((res) => res.json())
      .then((data) => setDevices(data))
      .catch((err) => console.log(err));
    fetch("/states")
      .then((res) => res.json())
      .then((data) => setStates(data))
      .catch((err) => console.log(err));
  }, []);

  console.log(devices); // Add this line

  function getState(device, board) {
    const deviceStates = states.filter((state) => state.Device === device.Name);
    for (const state of deviceStates) {
      const parameter = board.data.get(state.Device);
      if (parameter) {
        const conditionMet = eval(
          `${parameter} ${state.Condition} ${state.Parameter}`
        );
        if (conditionMet) {
          return state.Name;
        }
      }
    }
    return "Unknown";
  }

  return (
    <div className="dashboard-page">
      <h1>Dashboard</h1>
      {devices.map((device) => {
        const board = null; // Update this line
        return (
          <div key={device._id} className="dashboard-device">
            <h2>{device.Name}</h2>
            {board ? (
              <>
                <p>
                  State: <strong>{getState(device, board)}</strong>
                </p>
                <ul>
                  {Array.from(board.data.entries()).map(([key, value]) => (
                    <li key={key}>
                      {key}: {value}
                    </li>
                  ))}
                </ul>
              </>
            ) : (
              <p>No data available</p>
            )}
          </div>
        );
      })}
    </div>
  );
}
